resources.string:
precision mediump float;
varying vec4 vColor;
void main()
{
	gl_FragColor = vColor;
}

attribute vec4 vPosition;
attribute vec4 vNormal;

uniform mat4 uMVPMatrix;
uniform mat4 uMVMatrix;
uniform vec3 uLightPos;

varying vec4 vColor;
void main() {
    vec3 MVVertex = vec3(uMVMatrix * vPosition);
    vec3 MVNormal = vec3(uMVMatrix * vNormal);
    float distance = length(uLightPos - MVVertex);
    vec3 lightVector = normalize(uLightPos - MVVertex);
    float diffuse = max(dot(vec3(MVNormal), lightVector), 0.5);
    diffuse = diffuse * (1.0 / (1.0 + (0.0000001 * distance)));
    vColor = diffuse * vec4(1.0,1.0,0.0,1.0);
    vColor[3] = 1.0;
    gl_Position = uMVPMatrix * vPosition;
}
